mapper.select:
SELECT
,COUNT(file.file_id) as fileNum from sys_contract as contract LEFT JOIN sys_company as company on contract.company_id=company.company_id LEFT JOIN sys_contract_type as type on contract.contract_type_id=type.contractType_id LEFT JOIN sys_contract_file as file on file.contract_id=contract.contract_id where 1=1
/*status_code状态（1：未执行，2：在执行，3：即将过期，4：已经过期，5：已经删除）*/
and contract.status_code=1
and (contract.status_code=2 or contract.status_code=3)
and contract.status_code=3
and contract.status_code=4
and contract.status_code!=5
/*and TO_DAYS( contract.contract_endTime )- TO_DAYS(NOW()) BETWEEN 0 and 30*/
AND contract.company_id IN
#{item}
and contract.contract_startTime >= #{contractStartTime}
and contract.contract_endTime <= #{contractEndTime}
and contract.contract_type_id=#{contractTypeId}
and contract.contract_name like concat('%', #{contractName}, '%')
and contract.contract_code like concat('%', #{contractCode}, '%')
and contract.first_party_company like concat('%', #{firstPartyCompany}, '%')
and contract.second_party_company like concat('%', #{secondPartyCompany}, '%')
GROUP BY contract.contract_id ORDER BY contract.create_time DESC
limit #{offset},#{limit}

mapper.update:
update sys_contract set
status_code=1 where contract_startTime >= #{nowTime}
status_code=2 where contract_startTime <= #{nowTime} and contract_endTime >= #{nowTime} and status_code!=3
status_code=3 where TO_DAYS( contract_endTime )- TO_DAYS(NOW()) BETWEEN 0 and 30
status_code=4 where contract_endTime <= #{nowTime}

mapper.select:
SELECT count(contract.contract_id)
