mapper.select:
select
from sys_labor_cost as laborCost LEFT JOIN sys_labor_cost_details as details on laborCost.labor_cost_id=details.labor_cost_id LEFT JOIN sys_company as company on company.company_id=laborCost.company_id where 1=1
AND laborCost.company_id IN
#{item}
AND laborCost.company_id IN(SELECT company_id from sys_company where status_code!=2)
and laborCost.year=#{year}
and laborCost.month=#{month}
and laborCost.status_code!=2 GROUP BY laborCost.labor_cost_id order by laborCost.year desc,laborCost.month desc

select count(labor_cost_id) from (select
from sys_labor_cost as laborCost LEFT JOIN sys_labor_cost_details as details on laborCost.labor_cost_id=details.labor_cost_id LEFT JOIN sys_company as company on company.company_id=laborCost.company_id where 1=1
AND laborCost.company_id IN
#{item}
AND laborCost.company_id IN(SELECT company_id from sys_company where status_code!=2)
and laborCost.year=#{year}
and laborCost.month=#{month}
and laborCost.status_code!=2 GROUP BY laborCost.labor_cost_id) as a

mapper.update:
update sys_labor_cost_details set employee_total = #{employeeTotal},headcount_total = #{headcountTotal},entry_total = #{entryTotal},demission_total = #{demissionTotal},
labor_cost_total = #{laborCostTotal},pay_people_total = #{payPeopleTotal},begin_month_people = #{beginMonthPeople},month_deploy = #{monthDeploy}
where labor_cost_id = #{laborCostId} and department_type=#{departmentType}
