mapper.select:
SELECT SUM(sum.demission_total) as demission_total,SUM(sum.headcount_total)as headcount_total,SUM(sum.employee_total) as employee_total, SUM(sum.entry_total) AS entry_total,SUM(sum.labor_cost_total) AS labor_cost_total from (SELECT IFNULL(SUM(details.demission_total), 0) AS demission_total, IFNULL(SUM(details.headcount_total), 0) AS headcount_total, IFNULL(SUM(details.employee_total), 0) AS employee_total, IFNULL(SUM(details.entry_total), 0) AS entry_total, IFNULL(SUM(details.labor_cost_total), 0) AS labor_cost_total FROM sys_labor_cost AS laborCost LEFT JOIN sys_labor_cost_details AS details ON laborCost.labor_cost_id = details.labor_cost_id LEFT JOIN sys_company as company on company.company_id=laborCost.company_id where 1=1
and laborCost.company_id=#{companyId}
and laborCost.year=#{year} and laborCost.month=#{month} and laborCost.status_code!=2 GROUP BY laborCost.labor_cost_id) as sum

select

FROM sys_labor_cost AS laborCost
right JOIN sys_labor_cost_details AS details ON laborCost.labor_cost_id = details.labor_cost_id
where 1=1

    and laborCost.company_id=#{companyId}

and laborCost.year=#{year}
and laborCost.month=#{month}
GROUP BY details.department_type

mapper.update:
update sys_labor_cost_details set employee_total = #{employeeTotal},headcount_total = #{headcountTotal},entry_total = #{entryTotal},demission_total = #{demissionTotal},
labor_cost_total = #{laborCostTotal},department_type = #{departmentType}
where labor_cost_id = #{laborCostId}
