mapper.update:
update sys_project set project_unfinished_total = #{projectUnfinishedTotal},project_finished_total = #{projectFinishedTotal},
company_id = #{companyId},year = #{year},month = #{month},previous_file_mes = #{previousFileMes},accept_file_mes = #{acceptFileMes}
where project_id = #{projectId}

mapper.select:
SELECT project.project_id, project.company_id, project.`year`, project.`month`, project.project_finished_total, project.project_unfinished_total, project.create_time, project.status_code, energy.month_consumption_electricity, energy.month_consumption_water, company.company_name,COUNT(file.file_id) AS fileNum FROM sys_project AS project LEFT JOIN sys_energy AS energy ON project.project_id = energy.project_id LEFT JOIN sys_project_energy_file AS file ON project.project_id = file.project_id LEFT JOIN sys_company AS company ON company.company_id = project.company_id where 1=1 and project.status_code!=2 and project.year=#{year}
and project.month = #{month}
AND project.company_id IN
#{item}
AND project.company_id IN(SELECT company_id from sys_company where status_code!=2)
GROUP BY project.project_id order by project.year desc,project.month desc

SELECT COUNT(project_id) FROM sys_project AS project where 1=1
AND project.company_id IN
#{item}
AND project.company_id IN(SELECT company_id from sys_company where status_code!=2)
and project.status_code!=2 and project.year=#{year}

SELECT /*SUM(project.project_unfinished_total) AS yearProjectUnfinishedTotal, SUM(project.project_finished_total) AS yearProjectFinishedTotal,*/ SUM(month_consumption_electricity) as yearConsumptionElectricity, SUM(month_consumption_water) as yearConsumptionWater FROM sys_project AS project LEFT JOIN sys_energy AS energy ON project.project_id = energy.project_id WHERE 1=1 /*注：type为1时，为按区域查询（小区）查询数据，type为2时，不考虑登录用户权限内小区，查询全国数据，即是物业大屏数据展示分析接口使用*/
AND project.company_id IN
#{item}
AND project.company_id IN(SELECT company_id from sys_company where status_code!=2)
and project.year=#{year}
and project.status_code!=2

SELECT
sum(project.project_unfinished_total) AS projectUnfinishedTotal,
sum(project.project_finished_total) AS projectFinishedTotal
FROM sys_project AS project
WHERE 1=1
/*注：type为1时，为按区域查询（小区）查询数据，type为2时，不考虑登录用户权限内小区，查询全国数据，即是物业大屏数据展示分析接口使用*/


		AND project.company_id IN

			#{item}



		AND project.company_id IN(SELECT company_id from sys_company where status_code!=2)



and project.year=#{year}

	and project.month = #{month}

