mapper.select:
select
,company.company_name as companyName,COUNT(file.file_id) AS fileNum from sys_quality_manage as quality LEFT JOIN sys_company as company on quality.company_id=company.company_id LEFT JOIN sys_quality_manage_file AS file ON quality.quality_id = file.quality_id where 1=1
and quality.company_id=#{companyId}
and quality.year = #{year} and quality.status_code!=2 GROUP BY quality.quality_id ORDER BY quality.create_time DESC
limit #{offset},#{limit}

SELECT SUM(quality.quality_check) as qualityCheck, SUM(quality.quality_check_pass) as qualityCheckPass, SUM(quality.quality_check_fail) as qualityCheckFail, SUM( quality.quality_check_unmodified ) as qualityCheckUnmodified, SUM(quality.security_event) as securityEvent FROM sys_quality_manage AS quality WHERE 1 = 1
and quality.company_id=#{companyId}
and quality.year = #{year} and quality.status_code!=2

SELECT SUM(quality.quality_check) as qualityCheck, SUM(quality.quality_check_pass) as qualityCheckPass, SUM(quality.quality_check_fail) as qualityCheckFail, SUM( quality.quality_check_unmodified ) as qualityCheckUnmodified, SUM(quality.security_event) as securityEvent FROM sys_quality_manage AS quality WHERE 1 = 1
and quality.company_id=#{companyId}
and quality.year = #{year} and quality.month = #{month} and quality.status_code!=2

   SELECT

   FROM sys_quality_manage AS quality
   WHERE 1 = 1

       and quality.company_id=#{companyId}

   and quality.year = #{year}

and quality.status_code!=2

select count(quality_id)
from sys_quality_manage
where 1=1

    and company_id=#{companyId}

and year = #{year}
and status_code!=2

mapper.update:
update sys_quality_manage set company_id = #{companyId},year = #{year},month = #{month},quality_check = #{qualityCheck},quality_check_pass = #{qualityCheckPass}
,quality_check_fail = #{qualityCheckFail},security_event = #{securityEvent},quality_check_unmodified = #{qualityCheckUnmodified},create_time = #{createTime}
where quality_id = #{qualityId}
