mapper.select:
select /*group_concat(role.role_name) as roleName,*/
FROM sys_user as user LEFT JOIN sys_user_role as userRole on user.user_id=userRole.user_id LEFT JOIN sys_role as role on userRole.role_id=role.role_id LEFT JOIN sys_department as department on department.department_id=user.department_id LEFT JOIN sys_company as company on company.company_id=user.company_id where user.status_code!=3 order by user.create_time desc
limit #{offset},#{limit}

select distinct rm.menu_id from sys_user_role ur LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id LEFT JOIN sys_menu as menu on rm.menu_id=menu.menu_id where 1=1 and ur.user_id = #{userId}
and menu_type=1
and menu_type!=0
